def number_of_size
  new_hash = {}
  $journalists_list_init.each do |journalist|
    journalist_size = journalist.length - 1
    if new_hash[journalist_size].nil?
      new_hash[journalist_size] = 1
    else
      new_hash[journalist_size] += 1
    end
  end
  puts new_hash.sort.to_h
end

